<?phpnamespace App\Searchers\AppointmentSearch\Filters;use Illuminate\Database\Query\Builder;use App\Searchers\Filter as Filter;class DateRange implements Filter{    /**     * Apply a given search value to the builder instance.     *     * @param Builder $builder     * @param mixed $value     * @return Builder $builder     */    public static function apply(Builder $builder, $value)    {		return $builder->whereBetween( 'appointments.appointment_date', [ $value[0], date("Y-m-d",strtotime($value[1]."+1 day")) ] );    }}