<?phpnamespace App\Searchers\AppointmentSearch\Filters;use Illuminate\Database\Query\Builder;use App\Searchers\Filter as Filter;class StatusesId implements Filter{    /**     * Apply a given search value to the builder instance.     *     * @param Builder $builder     * @param mixed $value     * @return Builder $builder     */    public static function apply(Builder $builder, $value)    {		return $builder->whereIn( 'appointment_status_id', $value );    }}