<?phpnamespace App\Searchers\ClinicSearch\Filters;use Illuminate\Database\Eloquent\Builder;use App\Searchers\Filter as Filter;class SpecialityId implements Filter{    /**     * Apply a given search value to the builder instance.     *     * @param Builder $builder     * @param mixed $value     * @return Builder $builder     */    public static function apply(Builder $builder, $value)    {		return $builder->join('clinic_hcp_specialities', 'clinic_hcp_specialities.clinic_id', 'clinics.id' )									->join('hcp_specialities', 'hcp_specialities.id', 'clinic_hcp_specialities.hcp_speciality_id' )																		->select('clinics.*')						->distinct()						->whereIn( 'hcp_specialities.speciality_id', $value );    }}