<?phpnamespace App\Searchers\HCPSearch;use App\Clinic;use Illuminate\Http\Request;use Illuminate\Database\Query\Builder;use Illuminate\Support\Facades\DB;/** Source: https://m.dotdev.co/writing-advanced-eloquent-search-query-filters-de8b6c2598db*/class HCPSearch implements \App\Searchers\Searchable{    /*    |--------------------------------------------------------------------------    | HCP Search     |--------------------------------------------------------------------------    |    | This class builds the query to fetch the hcp based on the filters sent on	| the json request. It dynamics load the filter class from the \Filters\ folder    |    */ 		use \App\Searchers\SearchableTrait;	public static function new_query()	{		return DB::table('hcps')						->select(						'hcps.*'						)												->join('hcp_specialities', 'hcps.id', '=', 'hcp_specialities.hcp_id')						->join('clinic_hcp_specialities', 'clinic_hcp_specialities.hcp_speciality_id', '=', 'hcp_specialities.id' )						->distinct();	}			public static function filter_folder()	{		return __NAMESPACE__ . '\\Filters\\';	}	}