<?phpnamespace App\Searchers\ScheduleSearch\Filters;use Illuminate\Database\Query\Builder;use App\Searchers\Filter as Filter;class DayOfTheWeek implements Filter{    /**     * Apply a given search value to the builder instance.     *     * @param Builder $builder     * @param mixed $value     * @return Builder $builder     */    public static function apply(Builder $builder, $value)    {		return $builder->whereIn( 'day_of_the_week', $value );    }}