<?phpnamespace App\Searchers\SpecialitySearch;use App\Speciality;use Illuminate\Http\Request;use Illuminate\Database\Eloquent\Builder;/** Source: https://m.dotdev.co/writing-advanced-eloquent-search-query-filters-de8b6c2598db*/class SpecialitySearch implements \App\Searchers\Searchable{    /*    |--------------------------------------------------------------------------    | Speciality Search    |--------------------------------------------------------------------------    |    | This class builds the query to fetch the Specialitys based on the filters sent on	| the json request. It dynamics load the filter class from the \Filters\ folder    |    */		use \App\Searchers\SearchableTrait;	public static function new_model()	{		return new Speciality;	}		public static function filter_folder()	{		return __NAMESPACE__ . '\\Filters\\';	}}